package com.java.interview;

public class Prog24 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 2;
		int b = 4;
		
		System.out.println("Before swap: a = " + a + " b = " + b );
		
		b = b/a;
		a = a*b;
		
		System.out.println("After swap: a = " + a + " b = " + b );

	}

}
